import{_ as s,c as n,o as a,a as l}from"./app.f6b8b16b.js";const m=JSON.parse('{"title":"chkconfig","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8865\u5145\u8BF4\u660E","slug":"\u8865\u5145\u8BF4\u660E","link":"#\u8865\u5145\u8BF4\u660E","children":[{"level":3,"title":"\u8BED\u6CD5","slug":"\u8BED\u6CD5","link":"#\u8BED\u6CD5","children":[]},{"level":3,"title":"\u9009\u9879","slug":"\u9009\u9879","link":"#\u9009\u9879","children":[]},{"level":3,"title":"\u5B9E\u4F8B","slug":"\u5B9E\u4F8B","link":"#\u5B9E\u4F8B","children":[]}]}],"relativePath":"linux/command/pages/chkconfig.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/chkconfig.md"},p=l(`<h1 id="chkconfig" tabindex="-1">chkconfig <a class="header-anchor" href="#chkconfig" aria-hidden="true">#</a></h1><p>\u68C0\u67E5\u6216\u8BBE\u7F6E\u7CFB\u7EDF\u7684\u5404\u79CD\u670D\u52A1</p><h2 id="\u8865\u5145\u8BF4\u660E" tabindex="-1">\u8865\u5145\u8BF4\u660E <a class="header-anchor" href="#\u8865\u5145\u8BF4\u660E" aria-hidden="true">#</a></h2><p><strong>chkconfig\u547D\u4EE4</strong> \u68C0\u67E5\u3001\u8BBE\u7F6E\u7CFB\u7EDF\u7684\u5404\u79CD\u670D\u52A1\u3002\u8FD9\u662FRed\xA0Hat\u516C\u53F8\u9075\u5FAAGPL\u89C4\u5219\u6240\u5F00\u53D1\u7684\u7A0B\u5E8F\uFF0C\u5B83\u53EF\u67E5\u8BE2\u64CD\u4F5C\u7CFB\u7EDF\u5728\u6BCF\u4E00\u4E2A\u6267\u884C\u7B49\u7EA7\u4E2D\u4F1A\u6267\u884C\u54EA\u4E9B\u7CFB\u7EDF\u670D\u52A1\uFF0C\u5176\u4E2D\u5305\u62EC\u5404\u7C7B\u5E38\u9A7B\u670D\u52A1\u3002\u8C28\u8BB0chkconfig\u4E0D\u662F\u7ACB\u5373\u81EA\u52A8\u7981\u6B62\u6216\u6FC0\u6D3B\u4E00\u4E2A\u670D\u52A1\uFF0C\u5B83\u53EA\u662F\u7B80\u5355\u7684\u6539\u53D8\u4E86\u7B26\u53F7\u8FDE\u63A5\u3002</p><h3 id="\u8BED\u6CD5" tabindex="-1">\u8BED\u6CD5 <a class="header-anchor" href="#\u8BED\u6CD5" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">chkconfig</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">\u9009\u9879</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><h3 id="\u9009\u9879" tabindex="-1">\u9009\u9879 <a class="header-anchor" href="#\u9009\u9879" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">--add\uFF1A\u589E\u52A0\u6240\u6307\u5B9A\u7684\u7CFB\u7EDF\u670D\u52A1\uFF0C\u8BA9chkconfig\u6307\u4EE4\u5F97\u4EE5\u7BA1\u7406\u5B83\uFF0C\u5E76\u540C\u65F6\u5728\u7CFB\u7EDF\u542F\u52A8\u7684\u53D9\u8FF0\u6587\u4EF6\u5185\u589E\u52A0\u76F8\u5173\u6570\u636E\uFF1B</span></span>
<span class="line"><span style="color:#A6ACCD;">--del\uFF1A\u5220\u9664\u6240\u6307\u5B9A\u7684\u7CFB\u7EDF\u670D\u52A1\uFF0C\u4E0D\u518D\u7531chkconfig\u6307\u4EE4\u7BA1\u7406\uFF0C\u5E76\u540C\u65F6\u5728\u7CFB\u7EDF\u542F\u52A8\u7684\u53D9\u8FF0\u6587\u4EF6\u5185\u5220\u9664\u76F8\u5173\u6570\u636E\uFF1B</span></span>
<span class="line"><span style="color:#A6ACCD;">--level</span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">\u7B49\u7EA7\u4EE3\u53F7</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">\uFF1A\u6307\u5B9A\u8BFB\u7CFB\u7EDF\u670D\u52A1\u8981\u5728\u54EA\u4E00\u4E2A\u6267\u884C\u7B49\u7EA7\u4E2D\u5F00\u542F\u6216\u5173\u6BD5\u3002</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>\u7F3A\u7701\u7684\u8FD0\u884C\u7EA7\uFF0CRHS\u7528\u5230\u7684\u7EA7\u522B\u5982\u4E0B\uFF1A</p><ul><li>0\uFF1A\u5173\u673A</li><li>1\uFF1A\u5355\u7528\u6237\u6A21\u5F0F</li><li>2\uFF1A\u65E0\u7F51\u7EDC\u652F\u6301\u7684\u591A\u7528\u6237\u6A21\u5F0F</li><li>3\uFF1A\u6709\u7F51\u7EDC\u652F\u6301\u7684\u591A\u7528\u6237\u6A21\u5F0F</li><li>4\uFF1A\u4FDD\u7559\uFF0C\u672A\u4F7F\u7528</li><li>5\uFF1A\u6709\u7F51\u7EDC\u652F\u6301\u6709X-Window\u652F\u6301\u7684\u591A\u7528\u6237\u6A21\u5F0F</li><li>6\uFF1A\u91CD\u65B0\u5F15\u5BFC\u7CFB\u7EDF\uFF0C\u5373\u91CD\u542F</li></ul><p>\u5BF9\u5404\u4E2A\u8FD0\u884C\u7EA7\u7684\u8BE6\u7EC6\u89E3\u91CA\uFF1A</p><ul><li>0 \u4E3A\u505C\u673A\uFF0C\u673A\u5668\u5173\u95ED\u3002</li><li>1 \u4E3A\u5355\u7528\u6237\u6A21\u5F0F\uFF0C\u5C31\u50CFWin9x\u4E0B\u7684\u5B89\u5168\u6A21\u5F0F\u7C7B\u4F3C\u3002</li><li>2 \u4E3A\u591A\u7528\u6237\u6A21\u5F0F\uFF0C\u4F46\u662F\u6CA1\u6709NFS\u652F\u6301\u3002</li><li>3 \u4E3A\u5B8C\u6574\u7684\u591A\u7528\u6237\u6A21\u5F0F\uFF0C\u662F\u6807\u51C6\u7684\u8FD0\u884C\u7EA7\u3002</li><li>4 \u4E00\u822C\u4E0D\u7528\uFF0C\u5728\u4E00\u4E9B\u7279\u6B8A\u60C5\u51B5\u4E0B\u53EF\u4EE5\u7528\u5B83\u6765\u505A\u4E00\u4E9B\u4E8B\u60C5\u3002\u4F8B\u5982\u5728\u7B14\u8BB0\u672C \u7535\u8111\u7684\u7535\u6C60\u7528\u5C3D\u65F6\uFF0C\u53EF\u4EE5\u5207\u6362\u5230\u8FD9\u4E2A\u6A21\u5F0F\u6765\u505A\u4E00\u4E9B\u8BBE\u7F6E\u3002</li><li>5 \u5C31\u662FX11\uFF0C\u8FDB\u5230X Window\u7CFB\u7EDF\u4E86\u3002</li><li>6 \u4E3A\u91CD\u542F\uFF0C\u8FD0\u884Cinit 6\u673A\u5668\u5C31\u4F1A\u91CD\u542F\u3002</li></ul><p>\u9700\u8981\u8BF4\u660E\u7684\u662F\uFF0Clevel\u9009\u9879\u53EF\u4EE5\u6307\u5B9A\u8981\u67E5\u770B\u7684\u8FD0\u884C\u7EA7\u800C\u4E0D\u4E00\u5B9A\u662F\u5F53\u524D\u8FD0\u884C\u7EA7\u3002\u5BF9\u4E8E\u6BCF\u4E2A\u8FD0\u884C\u7EA7\uFF0C\u53EA\u80FD\u6709\u4E00\u4E2A\u542F\u52A8\u811A\u672C\u6216\u8005\u505C\u6B62\u811A\u672C\u3002\u5F53\u5207\u6362\u8FD0\u884C\u7EA7\u65F6\uFF0Cinit\u4E0D\u4F1A\u91CD\u65B0\u542F\u52A8\u5DF2\u7ECF\u542F\u52A8\u7684\u670D\u52A1\uFF0C\u4E5F\u4E0D\u4F1A\u518D\u6B21\u53BB\u505C\u6B62\u5DF2\u7ECF\u505C\u6B62\u7684\u670D\u52A1\u3002</p><p>\u8FD0\u884C\u7EA7\u6587\u4EF6\uFF1A</p><p>\u6BCF\u4E2A\u88ABchkconfig\u7BA1\u7406\u7684\u670D\u52A1\u9700\u8981\u5728\u5BF9\u5E94\u7684init.d\u4E0B\u7684\u811A\u672C\u52A0\u4E0A\u4E24\u884C\u6216\u8005\u66F4\u591A\u884C\u7684\u6CE8\u91CA\u3002\u7B2C\u4E00\u884C\u544A\u8BC9chkconfig\u7F3A\u7701\u542F\u52A8\u7684\u8FD0\u884C\u7EA7\u4EE5\u53CA\u542F\u52A8\u548C\u505C\u6B62\u7684\u4F18\u5148\u7EA7\u3002\u5982\u679C\u67D0\u670D\u52A1\u7F3A\u7701\u4E0D\u5728\u4EFB\u4F55\u8FD0\u884C\u7EA7\u542F\u52A8\uFF0C\u90A3\u4E48\u4F7F\u7528<code>-</code>\u4EE3\u66FF\u8FD0\u884C\u7EA7\u3002\u7B2C\u4E8C\u884C\u5BF9\u670D\u52A1\u8FDB\u884C\u63CF\u8FF0\uFF0C\u53EF\u4EE5\u7528<code>\\</code>\u8DE8\u884C\u6CE8\u91CA\u3002</p><p>\u4F8B\u5982random.init\u5305\u542B\u4E09\u884C\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#676E95;"># chkconfig: 2345 20 80</span></span>
<span class="line"><span style="color:#676E95;"># description: Saves and restores system entropy pool for \\</span></span>
<span class="line"><span style="color:#676E95;"># higher quality random number generation.</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="\u5B9E\u4F8B" tabindex="-1">\u5B9E\u4F8B <a class="header-anchor" href="#\u5B9E\u4F8B" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">chkconfig --list             </span><span style="color:#676E95;">#\u5217\u51FA\u6240\u6709\u7684\u7CFB\u7EDF\u670D\u52A1\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --add httpd        </span><span style="color:#676E95;">#\u589E\u52A0httpd\u670D\u52A1\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --del httpd        </span><span style="color:#676E95;">#\u5220\u9664httpd\u670D\u52A1\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --level httpd 2345 on        </span><span style="color:#676E95;">#\u8BBE\u7F6Ehttpd\u5728\u8FD0\u884C\u7EA7\u522B\u4E3A2\u30013\u30014\u30015\u7684\u60C5\u51B5\u4E0B\u90FD\u662Fon\uFF08\u5F00\u542F\uFF09\u7684\u72B6\u6001\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --list               </span><span style="color:#676E95;"># \u5217\u51FA\u7CFB\u7EDF\u6240\u6709\u7684\u670D\u52A1\u542F\u52A8\u60C5\u51B5\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --list mysqld        </span><span style="color:#676E95;"># \u5217\u51FAmysqld\u670D\u52A1\u8BBE\u7F6E\u60C5\u51B5\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig --level 35 mysqld on </span><span style="color:#676E95;"># \u8BBE\u5B9Amysqld\u5728\u7B49\u7EA73\u548C5\u4E3A\u5F00\u673A\u8FD0\u884C\u670D\u52A1\uFF0C--level 35\u8868\u793A\u64CD\u4F5C\u53EA\u5728\u7B49\u7EA73\u548C5\u6267\u884C\uFF0Con\u8868\u793A\u542F\u52A8\uFF0Coff\u8868\u793A\u5173\u95ED\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig mysqld on            </span><span style="color:#676E95;"># \u8BBE\u5B9Amysqld\u5728\u5404\u7B49\u7EA7\u4E3Aon\uFF0C\u201C\u5404\u7B49\u7EA7\u201D\u5305\u62EC2\u30013\u30014\u30015\u7B49\u7EA7\u3002</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">chkconfig \u2013level redis 2345 on </span><span style="color:#676E95;"># \u628Aredis\u5728\u8FD0\u884C\u7EA7\u522B\u4E3A2\u30013\u30014\u30015\u7684\u60C5\u51B5\u4E0B\u90FD\u662Fon\uFF08\u5F00\u542F\uFF09\u7684\u72B6\u6001\u3002</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><p>\u5982\u4F55\u589E\u52A0\u4E00\u4E2A\u670D\u52A1\uFF1A</p><ol><li>\u670D\u52A1\u811A\u672C\u5FC5\u987B\u5B58\u653E\u5728<code>/etc/ini.d/</code>\u76EE\u5F55\u4E0B\uFF1B</li><li><code>chkconfig --add servicename</code>\u5728chkconfig\u5DE5\u5177\u670D\u52A1\u5217\u8868\u4E2D\u589E\u52A0\u6B64\u670D\u52A1\uFF0C\u6B64\u65F6\u670D\u52A1\u4F1A\u88AB\u5728<code>/etc/rc.d/rcN.d</code>\u4E2D\u8D4B\u4E88K/S\u5165\u53E3\u4E86\uFF1B</li><li><code>chkconfig --level 35 mysqld on</code>\u4FEE\u6539\u670D\u52A1\u7684\u9ED8\u8BA4\u542F\u52A8\u7B49\u7EA7\u3002</li></ol>`,21),c=[p];function i(o,r,t,d,h,u){return a(),n("div",null,c)}const g=s(e,[["render",i]]);export{m as __pageData,g as default};
