import{_ as s,c as n,o as a,a as l}from"./app.f6b8b16b.js";const b=JSON.parse('{"title":"pfctl","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8865\u5145\u8BF4\u660E","slug":"\u8865\u5145\u8BF4\u660E","link":"#\u8865\u5145\u8BF4\u660E","children":[{"level":3,"title":"\u6FC0\u6D3B","slug":"\u6FC0\u6D3B","link":"#\u6FC0\u6D3B","children":[]},{"level":3,"title":"\u914D\u7F6E","slug":"\u914D\u7F6E","link":"#\u914D\u7F6E","children":[]},{"level":3,"title":"\u63A7\u5236","slug":"\u63A7\u5236","link":"#\u63A7\u5236","children":[]}]}],"relativePath":"linux/command/pages/pfctl.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/pfctl.md"},p=l(`<h1 id="pfctl" tabindex="-1">pfctl <a class="header-anchor" href="#pfctl" aria-hidden="true">#</a></h1><p>PF\u9632\u706B\u5899\u7684\u914D\u7F6E\u547D\u4EE4</p><h2 id="\u8865\u5145\u8BF4\u660E" tabindex="-1">\u8865\u5145\u8BF4\u660E <a class="header-anchor" href="#\u8865\u5145\u8BF4\u660E" aria-hidden="true">#</a></h2><p><strong>pfctl\u547D\u4EE4</strong> \u662FPF\u9632\u706B\u5899\u7684\u914D\u7F6E\u547D\u4EE4\uFF0CPF\u9632\u706B\u5899( \u5168\u79F0\uFF1APacket Filter )\u662FUNIX LIKE\u7CFB\u7EDF\u4E0A\u8FDB\u884CTCP/ip\u6D41\u91CF\u8FC7\u6EE4\u548C\u7F51\u7EDC\u5730\u5740\u8F6C\u6362\u7684\u8F6F\u4EF6\u7CFB\u7EDF\u3002PF\u540C\u6837\u4E5F\u80FD\u63D0\u4F9BTCP/IP\u6D41\u91CF\u7684\u6574\u5F62\u548C\u63A7\u5236\uFF0C\u5E76\u4E14\u63D0\u4F9B\u5E26\u5BBD\u63A7\u5236\u548C\u6570\u636E\u5305\u4F18\u5148\u96C6\u63A7\u5236\u3002PF\u6700\u65E9\u662F\u7531Daniel Hartmeier\u5F00\u53D1\u7684\uFF0C\u73B0\u5728\u7684\u5F00\u53D1\u548C\u7EF4\u62A4\u7531Daniel\u548CopenBSD\u5C0F\u7EC4\u7684\u5176\u4ED6\u6210\u5458\u8D1F\u8D23\u3002</p><p>PF\u9632\u706B\u5899\u7684\u529F\u80FD\u5F88\u591A\uFF0C\u672C\u7AD9\u53EA\u5217\u4E3E\u4E00\u4E9B\u57FA\u672C\u914D\u7F6E\u3002</p><h3 id="\u6FC0\u6D3B" tabindex="-1">\u6FC0\u6D3B <a class="header-anchor" href="#\u6FC0\u6D3B" aria-hidden="true">#</a></h3><p>\u8981\u6FC0\u6D3Bpf\u5E76\u4E14\u4F7F\u5B83\u5728\u542F\u52A8\u65F6\u8C03\u7528\u914D\u7F6E\u6587\u4EF6\uFF0C\u7F16\u8F91<code>/etc/rc.conf</code>\u6587\u4EF6\uFF0C\u4FEE\u6539\u914D\u7F6Epf\u7684\u4E00\u884C\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">pf=yes</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u91CD\u542F\u64CD\u4F5C\u7CFB\u7EDF\u8BA9\u914D\u7F6E\u751F\u6548\u3002</p><p>\u4E5F\u53EF\u4EE5\u901A\u8FC7pfctl\u7A0B\u5E8F\u542F\u52A8\u548C\u505C\u6B62pf\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">pfctl -e</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -d</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>\u6CE8\u610F\u8FD9\u4EC5\u4EC5\u662F\u542F\u52A8\u548C\u5173\u95EDPF\uFF0C\u5B9E\u9645\u5B83\u4E0D\u4F1A\u8F7D\u5165\u89C4\u5219\u96C6\uFF0C\u89C4\u5219\u96C6\u8981\u4E48\u5728\u7CFB\u7EDF\u542F\u52A8\u65F6\u8F7D\u5165\uFF0C\u8981\u5728PF\u542F\u52A8\u540E\u901A\u8FC7\u547D\u4EE4\u5355\u72EC\u8F7D\u5165\u3002</p><h3 id="\u914D\u7F6E" tabindex="-1">\u914D\u7F6E <a class="header-anchor" href="#\u914D\u7F6E" aria-hidden="true">#</a></h3><p>\u7CFB\u7EDF\u5F15\u5BFC\u5230\u5728rc\u811A\u672C\u6587\u4EF6\u8FD0\u884CPF\u65F6PF\u4ECE<code>/etc/pf.conf</code>\u6587\u4EF6\u8F7D\u5165\u914D\u7F6E\u89C4\u5219\u3002\u6CE8\u610F\u5F53<code>/etc/pf.conf</code>\u6587\u4EF6\u662F\u9ED8\u8BA4\u914D\u7F6E\u6587\u4EF6\uFF0C\u5728\u7CFB\u7EDF\u8C03\u7528rc\u811A\u672C\u6587\u4EF6\u65F6\uFF0C\u5B83\u4EC5\u4EC5\u662F\u4F5C\u4E3A\u6587\u672C\u6587\u4EF6\u7531pfctl\u88C5\u5165\u5E76\u89E3\u91CA\u548C\u63D2\u5165pf\u7684\u3002\u5BF9\u4E8E\u4E00\u4E9B\u5E94\u7528\u6765\u8BF4\uFF0C\u5176\u4ED6\u7684\u89C4\u5219\u96C6\u53EF\u4EE5\u5728\u7CFB\u7EDF\u5F15\u5BFC\u540E\u7531\u5176\u4ED6\u6587\u4EF6\u8F7D\u5165\u3002\u5BF9\u4E8E\u4E00\u4E9B\u8BBE\u8BA1\u7684\u975E\u5E38\u597D\u7684unix\u7A0B\u5E8F\uFF0CPF\u63D0\u4F9B\u4E86\u8DB3\u591F\u7684\u7075\u6D3B\u6027\u3002</p><p><strong>pf.conf\u6587\u4EF6\u67097\u4E2A\u90E8\u5206\uFF1A</strong></p><ol><li>\u5B8F\uFF1A\u7528\u6237\u5B9A\u4E49\u7684\u53D8\u91CF\uFF0C\u5305\u62ECIP\u5730\u5740\uFF0C\u63A5\u53E3\u540D\u79F0\u7B49\u7B49\u3002</li><li>\u8868\uFF1A\u4E00\u79CD\u7528\u6765\u4FDD\u5B58IP\u5730\u5740\u5217\u8868\u7684\u7ED3\u6784\u3002</li><li>\u9009\u9879\uFF1A\u63A7\u5236PF\u5982\u4F55\u5DE5\u4F5C\u7684\u53D8\u91CF\u3002</li><li>\u6574\u5F62\uFF1A\u91CD\u65B0\u5904\u7406\u6570\u636E\u5305\uFF0C\u8FDB\u884C\u6B63\u5E38\u5316\u548C\u788E\u7247\u6574\u7406\u3002</li><li>\u6392\u961F\uFF1A\u63D0\u4F9B\u5E26\u5BBD\u63A7\u5236\u548C\u6570\u636E\u5305\u4F18\u5148\u7EA7\u63A7\u5236\u3002</li><li>\u8F6C\u6362\uFF1A\u63A7\u5236\u7F51\u7EDC\u5730\u5740\u8F6C\u6362\u548C\u6570\u636E\u5305\u91CD\u5B9A\u5411\u3002</li><li>\u8FC7\u6EE4\u89C4\u5219\uFF1A\u5728\u6570\u636E\u5305\u901A\u8FC7\u63A5\u53E3\u65F6\u5141\u8BB8\u8FDB\u884C\u9009\u62E9\u6027\u7684\u8FC7\u6EE4\u548C\u963B\u6B62\u3002</li></ol><p>\u9664\u53BB\u5B8F\u548C\u8868\uFF0C\u5176\u4ED6\u7684\u6BB5\u5728\u914D\u7F6E\u6587\u4EF6\u4E2D\u4E5F\u5E94\u8BE5\u6309\u7167\u8FD9\u4E2A\u987A\u5E8F\u51FA\u73B0\uFF0C\u5C3D\u7BA1\u5BF9\u4E8E\u4E00\u4E9B\u7279\u5B9A\u7684\u5E94\u7528\u5E76\u4E0D\u662F\u6240\u6709\u7684\u6BB5\u90FD\u662F\u5FC5\u987B\u7684\u3002</p><p>\u7A7A\u884C\u4F1A\u88AB\u5FFD\u7565\uFF0C\u4EE5#\u5F00\u5934\u7684\u884C\u88AB\u8BA4\u4E3A\u662F\u6CE8\u91CA\u3002</p><h3 id="\u63A7\u5236" tabindex="-1">\u63A7\u5236 <a class="header-anchor" href="#\u63A7\u5236" aria-hidden="true">#</a></h3><p>\u5F15\u5BFC\u4E4B\u540E\uFF0CPF\u53EF\u4EE5\u901A\u8FC7pfctl\u7A0B\u5E8F\u8FDB\u884C\u64CD\u4F5C\uFF0C\u4EE5\u4E0B\u662F\u4E00\u4E9B\u4F8B\u5B50\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">pfctl -f /etc/pf.conf  </span><span style="color:#676E95;"># \u8F7D\u5165 pf.conf \u6587\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -nf /etc/pf.conf </span><span style="color:#676E95;"># \u89E3\u6790\u6587\u4EF6\uFF0C\u4F46\u4E0D\u8F7D\u5165</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -Nf /etc/pf.conf </span><span style="color:#676E95;"># \u53EA\u8F7D\u5165\u6587\u4EF6\u4E2D\u7684NAT\u89C4\u5219</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -Rf /etc/pf.conf </span><span style="color:#676E95;"># \u53EA\u8F7D\u5165\u6587\u4EF6\u4E2D\u7684\u8FC7\u6EE4\u89C4\u5219</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -sn </span><span style="color:#676E95;"># \u663E\u793A\u5F53\u524D\u7684NAT\u89C4\u5219</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -sr </span><span style="color:#676E95;"># \u663E\u793A\u5F53\u524D\u7684\u8FC7\u6EE4\u89C4\u5219</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -ss </span><span style="color:#676E95;"># \u663E\u793A\u5F53\u524D\u7684\u72B6\u6001\u8868</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -si </span><span style="color:#676E95;"># \u663E\u793A\u8FC7\u6EE4\u72B6\u6001\u548C\u8BA1\u6570</span></span>
<span class="line"><span style="color:#A6ACCD;">pfctl -sa </span><span style="color:#676E95;"># \u663E\u793A\u4EFB\u4F55\u53EF\u663E\u793A\u7684</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><p>\u5B8C\u6574\u7684\u547D\u4EE4\u5217\u8868\uFF0C\u8BF7\u53C2\u9605pfctl\u7684man\u624B\u518C\u9875\u3002</p>`,22),c=[p];function r(t,i,o,d,f,h){return a(),n("div",null,c)}const m=s(e,[["render",r]]);export{b as __pageData,m as default};
