import{_ as s,c as n,o as a,a as l}from"./app.f6b8b16b.js";const C=JSON.parse('{"title":"tftp","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8865\u5145\u8BF4\u660E","slug":"\u8865\u5145\u8BF4\u660E","link":"#\u8865\u5145\u8BF4\u660E","children":[{"level":3,"title":"\u8BED\u6CD5","slug":"\u8BED\u6CD5","link":"#\u8BED\u6CD5","children":[]},{"level":3,"title":"\u9009\u9879","slug":"\u9009\u9879","link":"#\u9009\u9879","children":[]},{"level":3,"title":"\u53C2\u6570","slug":"\u53C2\u6570","link":"#\u53C2\u6570","children":[]},{"level":3,"title":"\u5B9E\u4F8B","slug":"\u5B9E\u4F8B","link":"#\u5B9E\u4F8B","children":[]}]}],"relativePath":"linux/command/pages/tftp.md","lastUpdated":1666513677000}'),p={name:"linux/command/pages/tftp.md"},e=l(`<h1 id="tftp" tabindex="-1">tftp <a class="header-anchor" href="#tftp" aria-hidden="true">#</a></h1><p>\u5728\u672C\u673A\u548Ctftp\u670D\u52A1\u5668\u4E4B\u95F4\u4F7F\u7528TFTP\u534F\u8BAE\u4F20\u8F93\u6587\u4EF6</p><h2 id="\u8865\u5145\u8BF4\u660E" tabindex="-1">\u8865\u5145\u8BF4\u660E <a class="header-anchor" href="#\u8865\u5145\u8BF4\u660E" aria-hidden="true">#</a></h2><p><strong>tftp\u547D\u4EE4</strong> \u7528\u5728\u672C\u673A\u548Ctftp\u670D\u52A1\u5668\u4E4B\u95F4\u4F7F\u7528TFTP\u534F\u8BAE\u4F20\u8F93\u6587\u4EF6\u3002</p><p>TFTP\u662F\u7528\u6765\u4E0B\u8F7D\u8FDC\u7A0B\u6587\u4EF6\u7684\u6700\u7B80\u5355\u7F51\u7EDC\u534F\u8BAE\uFF0C\u5B83\u5176\u4E8EUDP\u534F\u8BAE\u800C\u5B9E\u73B0\u3002\u5D4C\u5165\u5F0Flinux\u7684tftp\u5F00\u53D1\u73AF\u5883\u5305\u62EC\u4E24\u4E2A\u65B9\u9762\uFF1A\u4E00\u662Flinux\u670D\u52A1\u5668\u7AEF\u7684tftp-server\u652F\u6301\uFF0C\u4E8C\u662F\u5D4C\u5165\u5F0F\u76EE\u6807\u7CFB\u7EDF\u7684tftp-client\u652F\u6301\u3002\u56E0\u4E3Au-boot\u672C\u8EAB\u5185\u7F6E\u652F\u6301tftp-client\uFF0C\u6240\u4EE5\u5D4C\u5165\u5F0F\u76EE\u6807\u7CFB\u7EDF\u7AEF\u5C31\u4E0D\u7528\u914D\u7F6E\u4E86\u3002\u4E0B\u9762\u5C31\u8BE6\u7EC6\u4ECB\u7ECD\u4E00\u4E0Blinux\u670D\u52A1\u5668\u7AEFtftp-server\u7684\u914D\u7F6E\u3002</p><h3 id="\u8BED\u6CD5" tabindex="-1">\u8BED\u6CD5 <a class="header-anchor" href="#\u8BED\u6CD5" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">tftp</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">\u9009\u9879</span><span style="color:#89DDFF;">)(</span><span style="color:#A6ACCD;">\u53C2\u6570</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><h3 id="\u9009\u9879" tabindex="-1">\u9009\u9879 <a class="header-anchor" href="#\u9009\u9879" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">-c\uFF1A\u6307\u5B9A\u4E0Etftp\u670D\u52A1\u5668\u8FDE\u63A5\u6210\u529F\u540E\uFF0C\u7ACB\u5373\u8981\u6267\u884C\u7684\u6307\u4EE4\uFF1B</span></span>
<span class="line"><span style="color:#A6ACCD;">-m\uFF1A\u6307\u5B9A\u6587\u4EF6\u4F20\u8F93\u6A21\u5F0F\u3002\u53EF\u4EE5\u662FASCII\u6216\u8005Binary\uFF1B</span></span>
<span class="line"><span style="color:#A6ACCD;">-v\uFF1A\u663E\u793A\u6307\u4EE4\u8BE6\u7EC6\u6267\u884C\u8FC7\u7A0B\uFF1B</span></span>
<span class="line"><span style="color:#A6ACCD;">-V\uFF1A\u663E\u793A\u6307\u4EE4\u7248\u672C\u4FE1\u606F\u3002</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="\u53C2\u6570" tabindex="-1">\u53C2\u6570 <a class="header-anchor" href="#\u53C2\u6570" aria-hidden="true">#</a></h3><p>\u4E3B\u673A\uFF1A\u6307\u5B9Atftp\u8981\u8054\u673A\u7684tftp\u670D\u52A1\u5668\u7684ip\u5730\u5740\u6216\u4E3B\u673A\u540D\u3002</p><h3 id="\u5B9E\u4F8B" tabindex="-1">\u5B9E\u4F8B <a class="header-anchor" href="#\u5B9E\u4F8B" aria-hidden="true">#</a></h3><p><strong>1\u3001\u5B89\u88C5tftp\u670D\u52A1\u5668</strong></p><p>\u9700\u8981\u5B89\u88C5xinetd\u3001tftp\u548Ctftp-server 3\u4E2A\u8F6F\u4EF6</p><p>\u5982\u679C\u80FD\u4E0A\u7F51\uFF0C\u901A\u8FC7yum\u5B89\u88C5\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">yum install xinetd</span></span>
<span class="line"><span style="color:#A6ACCD;">yum install tftp</span></span>
<span class="line"><span style="color:#A6ACCD;">yum install tftp-server</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>\u5982\u679C\u4E0D\u80FD\u4E0A\u7F51\uFF0C\u53EF\u4EE5\u76F4\u63A5\u5B89\u88C5\u63D0\u4F9B\u7684rpm\u5305\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">rpm -ivh xinetd-2.3.14-18.fc9.i386.rpm</span></span>
<span class="line"><span style="color:#A6ACCD;">rpm -ivh tftp-0.48-3.fc9.i386.rpm</span></span>
<span class="line"><span style="color:#A6ACCD;">rpm -ivh tftp-server-0.48-3.fc9.i386.rpm</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p><strong>2\u3001\u914D\u7F6Etftp\u670D\u52A1\u5668</strong></p><p>\u4FEE\u6539/etc/xinetd.d/tftp\u6587\u4EF6\uFF0C\u5C06\u5176\u4E2D\u7684disable=yes\u6539\u4E3Adisable=no\u3002\u4E3B\u8981\u662F\u8BBE\u7F6ETFTP\u670D\u52A1\u5668\u7684\u6839\u76EE\u5F55\uFF0C\u5F00\u542F\u670D\u52A1\u3002\u4FEE\u6539\u540E\u7684\u6587\u4EF6\u5982\u4E0B\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">service tftp</span></span>
<span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">       socket_type           =dgram</span></span>
<span class="line"><span style="color:#A6ACCD;">       protocol              =udp</span></span>
<span class="line"><span style="color:#A6ACCD;">       </span><span style="color:#82AAFF;">wait</span><span style="color:#A6ACCD;">                  =yes</span></span>
<span class="line"><span style="color:#A6ACCD;">       user                  =root</span></span>
<span class="line"><span style="color:#A6ACCD;">       server                =/usr/sbin/in.tftpd</span></span>
<span class="line"><span style="color:#A6ACCD;">       server_args           =-s  /home/mike/tftpboot -c</span></span>
<span class="line"><span style="color:#A6ACCD;">       disable               =no</span></span>
<span class="line"><span style="color:#A6ACCD;">       per_source            =11</span></span>
<span class="line"><span style="color:#A6ACCD;">       cps                   =100 2</span></span>
<span class="line"><span style="color:#A6ACCD;">       flags                 =IPv4</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br></div></div><p>\u8BF4\u660E\uFF1A\u4FEE\u6539\u9879<code>server_args= -s &lt;path&gt; -c</code>\uFF0C\u5176\u4E2Dpath\u5904\u53EF\u4EE5\u6539\u4E3A\u4F60\u7684 tftp-server\u7684\u6839\u76EE\u5F55\uFF0C\u53C2\u6570-s\u6307\u5B9Achroot\uFF0C-c\u6307\u5B9A\u4E86\u53EF\u4EE5\u521B\u5EFA\u6587\u4EF6\u3002</p><p><strong>3\u3001\u542F\u52A8tftp\u670D\u52A1\u5668\u5E76\u5173\u95ED\u9632\u706B\u5899</strong></p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">/etc/init.d/iptables stop        </span><span style="color:#676E95;"># \u5173\u95ED\u9632\u706B\u5899</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo /sbin/service xinetd start</span></span>
<span class="line"><span style="color:#A6ACCD;">\u6216</span></span>
<span class="line"><span style="color:#A6ACCD;">service xinetd restart</span></span>
<span class="line"><span style="color:#A6ACCD;">/etc/init.d/xinetd start</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>\u770B\u5230\u542F\u52A8[OK]\u5C31\u53EF\u4EE5\u4E86</p><p>4\u3001\u67E5\u770Btftp\u670D\u52A1\u662F\u5426\u5F00\u542F</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">netstat -a </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> grep tftp</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u663E\u793A\u7ED3\u679C\u4E3A<code>udp 0 0 *:tftp *:*</code>\u8868\u660E\u670D\u52A1\u5DF2\u7ECF\u5F00\u542F\uFF0C\u5C31\u8868\u660Etftp\u914D\u7F6E\u6210\u529F\u4E86\u3002</p><p><strong>5\u3001tftp\u4F7F\u7528</strong></p><p>\u590D\u5236\u4E00\u4E2A\u6587\u4EF6\u5230tftp\u670D\u52A1\u5668\u76EE\u5F55\uFF0C\u7136\u540E\u5728\u4E3B\u673A\u542F\u52A8tftp\u8F6F\u4EF6\uFF0C\u8FDB\u884C\u7B80\u5355\u6D4B\u8BD5\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">tftp 192.168.1.2</span></span>
<span class="line"><span style="color:#A6ACCD;">tftp</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">get </span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">download file</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">tftp</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">put </span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">upload file</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">tftp</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">q</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p><strong>6\u3001tftp\u547D\u4EE4\u7528\u6CD5\u5982\u4E0B</strong></p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">tftp your-ip-address</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u8FDB\u5165TFTP\u64CD\u4F5C\uFF1A</p><ul><li>connect\uFF1A\u8FDE\u63A5\u5230\u8FDC\u7A0Btftp\u670D\u52A1\u5668</li><li>mode\uFF1A\u6587\u4EF6\u4F20\u8F93\u6A21\u5F0F</li><li>put\uFF1A\u4E0A\u4F20\u6587\u4EF6</li><li>get\uFF1A\u4E0B\u8F7D\u6587\u4EF6</li><li>quit\uFF1A\u9000\u51FA</li><li>verbose\uFF1A\u663E\u793A\u8BE6\u7EC6\u7684\u5904\u7406\u4FE1\u606F</li><li>tarce\uFF1A\u663E\u793A\u5305\u8DEF\u5F84</li><li>status\uFF1A\u663E\u793A\u5F53\u524D\u72B6\u6001\u4FE1\u606F</li><li>binary\uFF1A\u4E8C\u8FDB\u5236\u4F20\u8F93\u6A21\u5F0F</li><li>ascii\uFF1Aascii \u4F20\u9001\u6A21\u5F0F</li><li>rexmt\uFF1A\u8BBE\u7F6E\u5305\u4F20\u8F93\u7684\u8D85\u65F6\u65F6\u95F4</li><li>timeout\uFF1A\u8BBE\u7F6E\u91CD\u4F20\u7684\u8D85\u65F6\u65F6\u95F4</li><li>help\uFF1A\u5E2E\u52A9\u4FE1\u606F</li><li>? \uFF1A\u5E2E\u52A9\u4FE1\u606F</li></ul><p><strong>7\u3001\u5982\u679C\u8001\u662F\u51FA\u73B0\u201CAVC Denial, click icon to view\u201D\u7684\u9519\u8BEF\uFF0C\u5E76\u4E0D\u80FD\u4F20\u8F93\u6587\u4EF6\uFF0C\u9700\u8981\u4F5C\u5982\u4E0B\u4FEE\u6539</strong></p><p>\u4FEE\u6539<code>/etc/sysconfig/selinux</code>,\u5C06SELINUX\u8BBE\u5B9A\u4E3Adisable\uFF0C\u4F7F\u7528\u547D\u4EE4<code>setenforce 0</code>\u8BA9selinux\u914D\u7F6E\u6587\u4EF6\u751F\u6548\u3002</p><p><strong>8\u3001Busybox\u4E2Dtftp\u547D\u4EE4\u7684\u7528\u6CD5</strong></p><p>\u547D\u4EE4\u683C\u5F0F\u4E3A\uFF1A</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">tftp </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">option</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;"> ... host </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">port</span><span style="color:#89DDFF;">]</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u5982\u679C\u8981\u4E0B\u8F7D\u6216\u4E0A\u4F20\u6587\u4EF6\u7684\u8BDD\u662F\u4E00\u5B9A\u8981\u7528\u8FD9\u4E9Boption\u7684\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">-g \u8868\u793A\u4E0B\u8F7D\u6587\u4EF6 </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">get</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">-p \u8868\u793A\u4E0A\u4F20\u6587\u4EF6 </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">put</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">-l \u8868\u793A\u672C\u5730\u6587\u4EF6\u540D </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">local file</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">-r \u8868\u793A\u8FDC\u7A0B\u4E3B\u673A\u7684\u6587\u4EF6\u540D </span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">remote file</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>\u4F8B\u5982\uFF0C\u8981\u4ECE\u8FDC\u7A0B\u4E3B\u673A192.168.1.2\u4E0A\u4E0B\u8F7D embedexpert\uFF0C\u5219\u5E94\u8F93\u5165\u4EE5\u4E0B\u547D\u4EE4</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">tftp -g -r embedexpert 192.168.1.2</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div>`,44),t=[e];function r(c,o,i,d,b,u){return a(),n("div",null,t)}const m=s(p,[["render",r]]);export{C as __pageData,m as default};
