import{_ as s,c as n,o as a,a as l}from"./app.f6b8b16b.js";const y=JSON.parse('{"title":"xz","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8865\u5145\u8BF4\u660E","slug":"\u8865\u5145\u8BF4\u660E","link":"#\u8865\u5145\u8BF4\u660E","children":[{"level":3,"title":"\u8BED\u6CD5","slug":"\u8BED\u6CD5","link":"#\u8BED\u6CD5","children":[]},{"level":3,"title":"\u9009\u9879","slug":"\u9009\u9879","link":"#\u9009\u9879","children":[]},{"level":3,"title":"\u53C2\u6570","slug":"\u53C2\u6570","link":"#\u53C2\u6570","children":[]},{"level":3,"title":"\u5B9E\u4F8B","slug":"\u5B9E\u4F8B","link":"#\u5B9E\u4F8B","children":[]}]}],"relativePath":"linux/command/pages/xz.md","lastUpdated":1666509709000}'),e={name:"linux/command/pages/xz.md"},p=l(`<h1 id="xz" tabindex="-1">xz <a class="header-anchor" href="#xz" aria-hidden="true">#</a></h1><p>POSIX \u5E73\u53F0\u5F00\u53D1\u5177\u6709\u9AD8\u538B\u7F29\u7387\u7684\u5DE5\u5177</p><h2 id="\u8865\u5145\u8BF4\u660E" tabindex="-1">\u8865\u5145\u8BF4\u660E <a class="header-anchor" href="#\u8865\u5145\u8BF4\u660E" aria-hidden="true">#</a></h2><p><strong>xz\u547D\u4EE4</strong> XZ Utils \u662F\u4E3A POSIX \u5E73\u53F0\u5F00\u53D1\u5177\u6709\u9AD8\u538B\u7F29\u7387\u7684\u5DE5\u5177\u3002\u5B83\u4F7F\u7528 LZMA2 \u538B\u7F29\u7B97\u6CD5\uFF0C\u751F\u6210\u7684\u538B\u7F29\u6587\u4EF6\u6BD4 POSIX \u5E73\u53F0\u4F20\u7EDF\u4F7F\u7528\u7684 gzip\u3001bzip2 \u751F\u6210\u7684\u538B\u7F29\u6587\u4EF6\u66F4\u5C0F\uFF0C\u800C\u4E14\u89E3\u538B\u7F29\u901F\u5EA6\u4E5F\u5F88\u5FEB\u3002\u6700\u521D XZ Utils \u7684\u662F\u57FA\u4E8E LZMA-SDK \u5F00\u53D1\uFF0C\u4F46\u662F LZMA-SDK \u5305\u542B\u4E86\u4E00\u4E9B WINDOWS \u5E73\u53F0\u7684\u7279\u6027\uFF0C\u6240\u4EE5 XZ Utils \u4E3A\u4EE5\u9002\u5E94 POSIX \u5E73\u53F0\u4F5C\u4E86\u5927\u5E45\u7684\u4FEE\u6539\u3002XZ Utils \u7684\u51FA\u73B0\u4E5F\u662F\u4E3A\u4E86\u53D6\u4EE3 POSIX \u7CFB\u7EDF\u4E2D\u65E7\u7684 LZMA Utils\u3002</p><h3 id="\u8BED\u6CD5" tabindex="-1">\u8BED\u6CD5 <a class="header-anchor" href="#\u8BED\u6CD5" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">xz</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">\u9009\u9879</span><span style="color:#89DDFF;">)(</span><span style="color:#A6ACCD;">\u53C2\u6570</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">xz </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">OPTION</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">... </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">FILE</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">...</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="\u9009\u9879" tabindex="-1">\u9009\u9879 <a class="header-anchor" href="#\u9009\u9879" aria-hidden="true">#</a></h3><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">-z, --compress    </span><span style="color:#676E95;"># \u5F3A\u5236\u538B\u7F29</span></span>
<span class="line"><span style="color:#A6ACCD;">-d, --decompress, --uncompress</span></span>
<span class="line"><span style="color:#89DDFF;">                  </span><span style="color:#676E95;"># force decompression</span></span>
<span class="line"><span style="color:#A6ACCD;">-t, --test        </span><span style="color:#676E95;"># \u6D4B\u8BD5\u538B\u7F29\u6587\u4EF6\u7684\u5B8C\u6574\u6027</span></span>
<span class="line"><span style="color:#A6ACCD;">-l, --list        </span><span style="color:#676E95;"># \u5217\u51FA\u6709\u5173.xz\u6587\u4EF6\u7684\u4FE1\u606F</span></span>
<span class="line"><span style="color:#A6ACCD;">-k, --keep        </span><span style="color:#676E95;"># \u4FDD\u7559\uFF08\u4E0D\u8981\u5220\u9664\uFF09\u8F93\u5165\u6587\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">-f, --force       </span><span style="color:#676E95;"># \u5F3A\u5236\u8986\u76D6\u8F93\u51FA\u6587\u4EF6\u548C\uFF08\u89E3\uFF09\u538B\u7F29\u94FE\u63A5</span></span>
<span class="line"><span style="color:#A6ACCD;">-c, --stdout, --to-stdout</span></span>
<span class="line"><span style="color:#89DDFF;">                  </span><span style="color:#676E95;"># \u5199\u5165\u6807\u51C6\u8F93\u51FA\uFF0C\u4E0D\u8981\u5220\u9664\u8F93\u5165\u6587\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">-0 ... -9         </span><span style="color:#676E95;"># \u538B\u7F29\u9884\u8BBE; \u9ED8\u8BA4\u4E3A6; \u53D6\u538B\u7F29\u673A*\u548C*</span></span>
<span class="line"><span style="color:#89DDFF;">                  </span><span style="color:#676E95;"># \u4F7F\u75287-9\u4E4B\u524D\u89E3\u538B\u7F29\u5185\u5B58\u4F7F\u7528\u91CF\u8003\u8651\u5728\u5185\uFF01</span></span>
<span class="line"><span style="color:#A6ACCD;">-e, --extreme     </span><span style="color:#676E95;"># \u5C1D\u8BD5\u901A\u8FC7\u4F7F\u7528\u66F4\u591A\u7684CPU\u65F6\u95F4\u6765\u63D0\u9AD8\u538B\u7F29\u6BD4;</span></span>
<span class="line"><span style="color:#89DDFF;">                  </span><span style="color:#676E95;"># \u8981\u6C42\u4E0D\u5F71\u54CD\u89E3\u538B\u7F29\u5B58\u50A8\u5668</span></span>
<span class="line"><span style="color:#A6ACCD;">-T, --threads=NUM </span><span style="color:#676E95;"># \u6700\u591A\u4F7F\u7528NUM\u4E2A\u7EBF\u7A0B; \u9ED8\u8BA4\u503C\u4E3A1;  set to 0</span></span>
<span class="line"><span style="color:#89DDFF;">                  </span><span style="color:#676E95;"># \u8BBE\u7F6E\u4E3A0\uFF0C\u4F7F\u7528\u4E0E\u5904\u7406\u5668\u5185\u6838\u4E00\u6837\u591A\u7684\u7EBF\u7A0B</span></span>
<span class="line"><span style="color:#A6ACCD;">-q, --quiet       </span><span style="color:#676E95;"># \u6291\u5236\u8B66\u544A; \u6307\u5B9A\u4E24\u6B21\u4EE5\u6291\u5236\u9519\u8BEF</span></span>
<span class="line"><span style="color:#A6ACCD;">-v, --verbose     </span><span style="color:#676E95;"># \u5197\u957F; \u6307\u5B9A\u4E24\u6B21\u66F4\u8BE6\u7EC6</span></span>
<span class="line"><span style="color:#A6ACCD;">-h, --help        </span><span style="color:#676E95;"># \u663E\u793A\u8FD9\u4E2A\u7B80\u6D01\u7684\u5E2E\u52A9\u5E76\u9000\u51FA</span></span>
<span class="line"><span style="color:#A6ACCD;">-H, --long-help   </span><span style="color:#676E95;"># \u663E\u793A\u66F4\u591A\u5E2E\u52A9\uFF08\u8FD8\u5217\u51FA\u4E86\u9AD8\u7EA7\u9009\u9879\uFF09</span></span>
<span class="line"><span style="color:#A6ACCD;">-V, --version     </span><span style="color:#676E95;"># \u663E\u793A\u7248\u672C\u53F7\u5E76\u9000\u51FA</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br></div></div><h3 id="\u53C2\u6570" tabindex="-1">\u53C2\u6570 <a class="header-anchor" href="#\u53C2\u6570" aria-hidden="true">#</a></h3><ul><li>\u6E90\u6587\u4EF6\uFF1A\u6307\u5B9A\u8FDE\u63A5\u7684\u6E90\u6587\u4EF6\u3002</li><li>\u76EE\u6807\u6587\u4EF6\uFF1A\u6307\u5B9A\u6E90\u6587\u4EF6\u7684\u76EE\u6807\u8FDE\u63A5\u6587\u4EF6\u3002</li></ul><h3 id="\u5B9E\u4F8B" tabindex="-1">\u5B9E\u4F8B <a class="header-anchor" href="#\u5B9E\u4F8B" aria-hidden="true">#</a></h3><p>\u538B\u7F29\u4E00\u4E2A\u6587\u4EF6 test.txt\uFF0C\u538B\u7F29\u6210\u529F\u540E\u751F\u6210 test.txt.xz, \u539F\u6587\u4EF6\u4F1A\u88AB\u5220\u9664\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ xz test.txt</span></span>
<span class="line"><span style="color:#A6ACCD;">$ ls test.txt</span><span style="color:#89DDFF;">*</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">test.txt.xz</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>\u89E3\u538B test.txt.xz \u6587\u4EF6\uFF0C\u5E76\u4F7F\u7528\u53C2\u6570 -k \u4FDD\u6301\u539F\u6587\u4EF6\u4E0D\u88AB\u5220\u9664</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ xz -d -k test.txt.xz</span></span>
<span class="line"><span style="color:#A6ACCD;">$ ls test.txt</span><span style="color:#89DDFF;">*</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">test.txt.xz test.txt</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>\u4F7F\u7528\u53C2\u6570 -l \u663E\u793A .xz \u6587\u4EF6\u7684\u57FA\u672C\u4FE1\u606F\u3002\u57FA\u672C\u4FE1\u606F\u5305\u62EC\u538B\u7F29\u7387\u3001\u6570\u636E\u5B8C\u6574\u6027\u9A8C\u8BC1\u65B9\u5F0F\u7B49\u3002\u4E5F\u53EF\u4EE5\u548C\u53C2\u6570 -v \u6216 -vv \u914D\u5408\u663E\u793A\u66F4\u8BE6\u5C3D\u7684\u4FE1\u606F\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">xz -l index.txt.xz</span></span>
<span class="line"><span style="color:#676E95;"># Strms  Blocks   Compressed Uncompressed  Ratio  Check   Filename</span></span>
<span class="line"><span style="color:#676E95;">#    1       1        768 B      1,240 B  0.619  CRC64   index.txt.</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>\u4F7F\u7528\u53C2\u6570 -0, -1, -2, \u2026 -6, \u2026 -9 \u6216\u53C2\u6570 --fast, --best \u8BBE\u5B9A\u538B\u7F29\u7387\u3002xz \u547D\u4EE4\u7684\u9ED8\u8BA4\u4E3A -6 \uFF0C\u5BF9\u4E8E\u5927\u591A\u6570\u7CFB\u7EDF\u6765\u8BF4\uFF0C\u751A\u81F3\u662F\u4E00\u4E9B\u8F83\u65E7\u7684\u7CFB\u7EDF\uFF0C-4 \u2026 -6 \u538B\u7F29\u7387\u9884\u8BBE\u503C\u90FD\u4E0D\u9519\u7684\u8868\u73B0\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ xz -k7 xz_pipe_decomp_mini.c</span></span>
<span class="line"><span style="color:#A6ACCD;">$ xz -k --fast xz_pipe_decomp_mini.c</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>\u4F7F\u7528\u53C2\u6570 -H \u663E\u793A xz \u547D\u4EE4\u6240\u6709 options. \u53C2\u6570 -H \u6BD4\u4F7F\u7528\u53C2\u6570 --help \u663E\u793A\u7684\u5185\u5BB9\u66F4\u8BE6\u7EC6\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#A6ACCD;">$ xz -H  </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> more</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u501F\u52A9 xargs \u547D\u4EE4\u5E76\u884C\u538B\u7F29\u591A\u6587\u4EF6\u3002\u4E0B\u9762\u7684\u547D\u4EE4\u884C\u53EF\u4EE5\u5C06 /var/log \u76EE\u5F55\u4E0B\u6240\u6709\u7684\u6269\u5C55\u540D\u4E3A .log \u7684\u6587\u4EF6\u538B\u7F29\u3002\u901A\u8FC7 xargs \u547D\u4EE4\u540C\u65F6\u8FD0\u884C\u591A\u4E2A xz \u8FDB\u884C\u538B\u7F29\u3002</p><div class="language-shell line-numbers-mode"><button class="copy"></button><span class="lang">shell</span><pre><code><span class="line"><span style="color:#676E95;"># \u8FD0\u884C\u6B64\u547D\u4EE4\u987B\u6709 root \u6743\u9650\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">find /var/log -type f -iname </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">*.log</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> -print0 </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> xargs -P4 -n16 xz -T1</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,23),r=[p];function c(o,t,i,d,b,u){return a(),n("div",null,r)}const C=s(e,[["render",c]]);export{y as __pageData,C as default};
